// SetFriendships removes all previously related items of the
// user replacing them completely with the passed
// in related items, optionally inserting them as new records.
// Sets o.R.Users's Friendships accordingly.
// Replaces o.R.Friendships with related.
// Sets related.R.Users's Friendships accordingly.
func (o *User) SetFriendships(ctx context.Context, exec boil.ContextExecutor, insert bool, related ...*Friendship) error {
	query := "delete from `user_has_friendship` where `user_id` = ?"
	values := []interface{}{o.ID}
	if boil.IsDebug(ctx) {
		writer := boil.DebugWriterFrom(ctx)
		fmt.Fprintln(writer, query)
		fmt.Fprintln(writer, values)
	}
	_, err := exec.ExecContext(ctx, query, values...)
	if err != nil {
		return errors.Wrap(err, "failed to remove relationships before set")
	}

	removeFriendshipsFromUsersSlice(o, related)
	if o.R != nil {
		o.R.Friendships = nil
	}
	return o.AddFriendships(ctx, exec, insert, related...)
}

// RemoveFriendships relationships from objects passed in.
// Removes related items from R.Friendships (uses pointer comparison, removal does not keep order)
// Sets related.R.Users.
func (o *User) RemoveFriendships(ctx context.Context, exec boil.ContextExecutor, related ...*Friendship) error {
	var err error
	query := fmt.Sprintf(
		"delete from `user_has_friendship` where `user_id` = ? and `friendship_id` in (%s)",
		strmangle.Placeholders(dialect.UseIndexPlaceholders, len(related), 2, 1),
	)
	values := []interface{}{o.ID}
	for _, rel := range related {
		values = append(values, rel.ID)
	}

	if boil.IsDebug(ctx) {
		writer := boil.DebugWriterFrom(ctx)
		fmt.Fprintln(writer, query)
		fmt.Fprintln(writer, values)
	}
	_, err = exec.ExecContext(ctx, query, values...)
	if err != nil {
		return errors.Wrap(err, "failed to remove relationships before set")
	}
	removeFriendshipsFromUsersSlice(o, related)
	if o.R == nil {
		return nil
	}

	for _, rel := range related {
		for i, ri := range o.R.Friendships {
			if rel != ri {
				continue
			}

			ln := len(o.R.Friendships)
			if ln > 1 && i < ln-1 {
				o.R.Friendships[i] = o.R.Friendships[ln-1]
			}
			o.R.Friendships = o.R.Friendships[:ln-1]
			break
		}
	}

	return nil
}

func removeFriendshipsFromUsersSlice(o *User, related []*Friendship) {
	for _, rel := range related {
		if rel.R == nil {
			continue
		}
		for i, ri := range rel.R.Users {
			if o.ID != ri.ID {
				continue
			}

			ln := len(rel.R.Users)
			if ln > 1 && i < ln-1 {
				rel.R.Users[i] = rel.R.Users[ln-1]
			}
			rel.R.Users = rel.R.Users[:ln-1]
			break
		}
	}
}

